#include <stdio.h>

#define LIMIT 10

void printArray(int array[], int size)
{
    for (int i = 0; i < size; i++)
    {
        printf("%d ", array[i]);
    }
    printf("\n");
}

void insertionSort(int array[], int l, int h)
{
    for (int step = l + 1; step <= h; step++)
    {
        int key = array[step];
        int j = step - 1;

        while (key < array[j] && j >= 0)
        {
            array[j + 1] = array[j];
            --j;
        }

        array[j + 1] = key;
    }
}

void merge(int arr[], int l, int m, int h)
{
    int size = h - l + 1;
    if (size < LIMIT)
    {
        insertionSort(arr, l, h);
        return;
    }

    int i, j, k;
    int n1 = m;
    int n2 = h;

    int output[size];

    i = l;
    j = m;
    k = 0;

    printf("%d %d %d %d %d\n", i, j, k, n1, n2);

    while (i < n1 && j < n2)
    {
        if (arr[i] <= arr[j])
        {
            output[k] = arr[i];
            i++;
        }
        else
        {
            output[k] = arr[j];
            j++;
        }
        k++;
    }

    while (i < n1)
    {
        output[k] = arr[i];
        i++;
        k++;
    }

    while (j < n2)
    {
        output[k] = arr[j];
        j++;
        k++;
    }

    for (i = l; i < h; i++)
        arr[i] = output[i];
}

void mergeSort(int arr[], int size)
{
    int notSorted = 1, subIsSorted = 0, l = 0, m, h, i;

    while (notSorted)
    {
        notSorted = 0;
        subIsSorted = 1;
        if (l >= size || m >= size)
            l = 0;

        m = l + 1;

        while (m < size && arr[m] <= arr[m + 1])
            m++;

        if (m < size)
        {
            h = m + 1;
            while (h < size && arr[h] <= arr[h + 1])
                h++;
        }

        for (i = l; i < h; i++)
            if (arr[i] < arr[i + 1])
                subIsSorted = 0;

        if (!subIsSorted)
            merge(arr, l, m, h);

        i = 0;
        while (arr[i] <= arr[i + 1])
            i++;

        l = h;
        notSorted = i < size;
    }
}

